    <script class="js">
      PopUp()
        //SlideUp();
        window.MyFunc={
            i : 1,
            AutoToggle : function(){
               
                setInterval(()=>{
                  // CreateNotify('error',i)
                     let total=document.querySelectorAll('.flyer').length;
                    document.querySelectorAll('.flyer').forEach((flyer)=>{
                        flyer.classList.remove('active');
                    });
                    document.querySelectorAll('.tracker').forEach((tracker)=>{
                        tracker.classList.remove('active');
                    });
                    document.querySelector(`.flyer-${MyFunc.i >= 3 ? 1 : MyFunc.i+1}`).classList.add('active');
                     document.querySelector(`.tracker-${MyFunc.i >= 3 ? 1 : MyFunc.i+1}`).classList.add('active');
                   MyFunc.i=MyFunc.i >= total ? 1 : MyFunc.i+1;
                },5000)
            },
            Prompt : function(response){
                SlideUp(response)
            },
            Confirmed : function(response,event){
                let data=JSON.parse(response);
                CreateNotify(data.status,data.message);
                if(data.status == 'success'){
                    HideSlideUp();
                    spa(event,data.url);
                    
                }
            },
            CheckedIn : function(response,event){
                let data=JSON.parse(response);
                CreateNotify(data.status,data.message);
                if(data.status == 'success'){
                    spa(event,data.url);
                }
            },
            PopUpHidden : function(){
                sessionStorage.setItem('popped_up','true');
            },
            PopUp : function(){
                let popped_up=sessionStorage.getItem('popped_up') ?? false;
                if(!popped_up){
                    PopUp();
                }
            }
        }
        MyFunc.AutoToggle();
        MyFunc.PopUp();
    </script>
@endsection
