    <script class="js">
        window.MyFunc = {
            Updated : function(response){
                let data=JSON.parse(response);
                if(data.status == 'success'){
                    HideSlideUp();
                    document.querySelector('.password_form').reset();
                }
            },
            Loaded : function(){
                document.querySelector('.profile-house').style.paddingBottom=(document.querySelector('.wallets').offsetHeight)/2 + 'px';
                 document.querySelector('.profile-house').style.marginBottom=(document.querySelector('.wallets').offsetHeight)/2 + 'px'
            }
        }
        MyFunc.Loaded();
    </script>
